void drawScore(int x, int y, int value, int color)
{
	int n = value;
	int i = 0;

	loadfont(score, '0', '9');
	setfontsize(50, 8, 5, 8);
	setcolor(color);

	if (n == 0)
	{
		drawchar('0', x - 3, y);
	}
	else
	{
		while (n)
		{
			bufferScore[i] = n % 10 + 48;
			i++;
			n = n / 10;
		}
		x = x - (i * 3); // center text
		i--;
		for (i; i >= 0; i--)
		{
			drawchar(bufferScore[i], x, y);
			x += 6;
		}
	}
}

void drawScoreMini(int x, int y, int value, int align)
{ // 0 left 1 right
	int n = value;
	int i = 0;

	loadfont(scoreMini, '0', '9');
	setfontsize(40, 8, 4, 8);
	setcolor(4);

	if (n == 0)
	{
		drawchar('0', x, y);
	}
	else
	{
		while (n)
		{
			bufferScore[i] = n % 10 + 48;
			i++;
			n = n / 10;
		}

		if (align == 1)
		{ // align right
			x = x - (i * 4);
		}

		i--;
		for (i; i >= 0; i--)
		{
			drawchar(bufferScore[i], x, y);
			x += 5;
		}
	}
}

